import React, { useState } from 'react';
import Navbar from './components/Navbar';
import Tasks from './components/Tasks';
import GoogleCalendar from './components/GoogleCalendar';

function App() {
  const [question, setQuestion] = useState('');
  const [answer, setAnswer] = useState('');

  const handleAsk = async () => {
    if (!question) return;
    try {
      const res = await fetch('http://localhost:5000/api/query', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ question })
      });
      const data = await res.json();
      setAnswer(data.answer);
    } catch (error) {
      setAnswer('Error: Could not reach the server');
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 pt-16 font-sans">
      <Navbar />

      <div className="container mx-auto p-6 space-y-10">

        {/* RAG Section */}
        <section id="home" className="p-6 bg-white rounded-2xl shadow-lg">
          <h2 className="text-3xl font-bold mb-6 text-gray-800">Ask the Intelligent RAG System</h2>
          <div className="flex flex-col md:flex-row gap-4 mb-6">
            <input
              type="text"
              placeholder="Type your question..."
              value={question}
              onChange={(e) => setQuestion(e.target.value)}
              className="flex-1 p-4 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-indigo-500"
            />
            <button
              onClick={handleAsk}
              className="bg-indigo-500 text-white px-6 py-4 rounded-xl hover:bg-indigo-600 transition font-semibold shadow-md"
            >
              Ask
            </button>
          </div>
          {answer && (
            <div className="mt-4 p-5 bg-indigo-50 border-l-4 border-indigo-500 rounded-lg shadow-inner">
              <h3 className="font-semibold mb-2 text-gray-700">Answer:</h3>
              <p className="text-gray-800">{answer}</p>
            </div>
          )}
        </section>

        {/* Tasks Section */}
        <section id="tasks">
          <Tasks />
        </section>

        {/* Google Calendar Section */}
        <section id="calendar">
          <GoogleCalendar />
        </section>

      </div>
    </div>
  );
}

export default App;
