import React, { useState, useEffect } from 'react';

const Tasks = () => {
  // استرجاع المهام من localStorage إذا وجدت
  const [tasks, setTasks] = useState(() => {
    const saved = localStorage.getItem('tasks');
    return saved ? JSON.parse(saved) : [];
  });
  const [taskText, setTaskText] = useState('');

  // حفظ المهام تلقائيًا عند التغيير
  useEffect(() => {
    localStorage.setItem('tasks', JSON.stringify(tasks));
  }, [tasks]);

  // إضافة مهمة جديدة
  const addTask = () => {
    if (!taskText.trim()) return;
    setTasks([
      ...tasks,
      { id: Date.now(), text: taskText.trim(), completed: false },
    ]);
    setTaskText('');
  };

  // حذف مهمة
  const removeTask = (id) => {
    setTasks(tasks.filter((task) => task.id !== id));
  };

  // تعديل مهمة
  const toggleComplete = (id) => {
    setTasks(
      tasks.map((task) =>
        task.id === id ? { ...task, completed: !task.completed } : task
      )
    );
  };

  return (
    <div className="mb-8 p-4 bg-white rounded shadow">
      <h3 className="font-semibold text-lg mb-4">Tasks</h3>

      {/* إضافة مهمة */}
      <div className="flex gap-2 mb-4">
        <input
          type="text"
          placeholder="New task..."
          value={taskText}
          onChange={(e) => setTaskText(e.target.value)}
          className="border p-2 rounded flex-1 focus:outline-none focus:ring-2 focus:ring-blue-400"
        />
        <button
          onClick={addTask}
          className="bg-green-500 hover:bg-green-600 text-white px-4 py-2 rounded transition"
        >
          Add
        </button>
      </div>

      {/* قائمة المهام */}
      {tasks.length === 0 ? (
        <p className="text-gray-500">No tasks yet. Add a task to get started!</p>
      ) : (
        <ul className="space-y-2">
          {tasks.map((task) => (
            <li
              key={task.id}
              className="flex justify-between items-center bg-gray-50 p-2 rounded hover:bg-gray-100 transition"
            >
              <div className="flex items-center gap-2">
                <input
                  type="checkbox"
                  checked={task.completed}
                  onChange={() => toggleComplete(task.id)}
                  className="h-4 w-4"
                />
                <span className={task.completed ? 'line-through text-gray-400' : ''}>
                  {task.text}
                </span>
              </div>
              <button
                onClick={() => removeTask(task.id)}
                className="bg-red-500 hover:bg-red-600 text-white px-2 py-1 rounded transition text-sm"
              >
                Delete
              </button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Tasks;
